// CRUDCategorie.jsx

import React, { useState } from 'react';
import Header from './Header';

const CRUDCategorie = () => {
  // État pour stocker la liste des catégories
  const [categories, setCategories] = useState([
    { id: 1, name: 'Sedan' },
    { id: 2, name: 'SUV' },
    { id: 3, name: 'Truck' },
  ]);

  // État pour suivre la nouvelle catégorie entrée dans le formulaire
  const [newCategory, setNewCategory] = useState('');

  // Fonction pour ajouter une nouvelle catégorie
  const addCategory = () => {
    if (newCategory.trim() !== '') {
      setCategories([...categories, { id: categories.length + 1, name: newCategory }]);
      setNewCategory('');
    }
  };

  // Fonction pour supprimer une catégorie
  const deleteCategory = (categoryId) => {
    const updatedCategories = categories.filter((category) => category.id !== categoryId);
    setCategories(updatedCategories);
  };

  return (
    <>
    <Header></Header>
    <div className="container mt-5">
      <h2>CRUD Catégorie de Voiture</h2>

      <form className="mb-3">
        <div className="input-group">
          <input
            type="text"
            className="form-control"
            placeholder="Nouvelle Catégorie"
            value={newCategory}
            onChange={(e) => setNewCategory(e.target.value)}
          />
          <button type="button" className="btn btn-primary" onClick={addCategory}>
            Ajouter
          </button>
        </div>
      </form>

      <ul className="list-group">
        {categories.map((category) => (
          <li key={category.id} className="list-group-item d-flex justify-content-between align-items-center">
            {category.name}
            <button
              type="button"
              className="btn btn-danger"
              onClick={() => deleteCategory(category.id)}
            >
              Supprimer
            </button>
          </li>
        ))}
      </ul>
    </div>
    </>
  );
};

export default CRUDCategorie;
